import ExpenseItem from "./ExpenseItem";
import "./ExpenseDisplay.css";
import ExpensesFilter from "./filter/Filter";
import Card from "./Card";

import {useState} from 'react';


function ExpenseDisplay(props) {
    const [year, setYear] = useState('');

        const saveFilteredYearHandler =(year) => {
            setYear(year);
          console.log('year ' + year + ' is in the house!')
        }
 

    return (
        <div className="expenses">
            <Card>
            <ExpensesFilter onSaveFilter={saveFilteredYearHandler} />
            </Card>
         <ExpenseItem title={props.expenses[0].title}
        amount={props.expenses[0].amount}
        date={props.expenses[0].date} />
     <ExpenseItem title={props.expenses[1].title}
        amount={props.expenses[1].amount}
        date={props.expenses[1].date} />
        <ExpenseItem title={props.expenses[2].title}
        amount={props.expenses[2].amount}
        date={props.expenses[2].date} />
        <ExpenseItem title={props.expenses[3].title}
        amount={props.expenses[3].amount}
        date={props.expenses[3].date} />
       
        </div>
    )
}

export default ExpenseDisplay;
